import React, { Component } from 'react';
import * as firebase from 'firebase';
import './LeftPanelCompany.css';
import Login from './login';
import {
    BrowserRouter as Router,
    Route,
    Link
} from 'react-router-dom';

import MyPostJobs from './MyPostJobs'
// import JobPost from './JobPost';



class LeftPanelCompany extends Component {
    constructor() {
        super();
        this.state = {
            user: null
        }

    }
    componentDidMount() {
        firebase.auth().onAuthStateChanged(() => {
            var userId = firebase.auth().currentUser.uid;
            const rootRef = firebase.database().ref();
            const speedRef = rootRef.child('USER/' + userId);
            speedRef.on('value', snap => {
                var userName = snap.val().name
                console.log(userName);
                this.setState({ user: userName })
            });
        })
    }

    render() {
        return (
            <Router>
                <div>
                    {this.state.user}
                    <div id="left-navigation">
                        <h1>Comapny</h1>
                        <h2>{this.state.user}</h2>
                        {/* <img src="title.jpg" alt="logo" height="250" width="250"/> */}
                        {/* <p><Link className="link" to="/Company/JobPost" >Post Job</Link></p> */}
                        <p><Link className="link" to="/Company/MyPostJobs" >My Jobs</Link></p>
                        <p><Link className="link" to="Comapny/login" >Login</Link></p>
                        {/* <p><Link className="link" to="/ViewAllStudents" >View All Students</Link></p> */}

                    </div>

                    <h4>      hi    </h4>


                    {/* <Route path="/Company/JobPost" Component={JobPost} /> */}
                    <Route path="/Company/login" Component={log} />
                    <Route path="/Company/MyPostJobs" Component={MyPostJobs} />
                </div>
            </Router>
        );
    }

}
export default LeftPanelCompany;
