import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import {Button} from 'react-bootstrap'
import EditIcon from 'material-ui/svg-icons/image/edit'

import RaisedButton from 'material-ui/RaisedButton';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
// import {
//   Table,
//   TableBody,
//   TableHeader,
//   TableHeaderColumn,
//   TableRow,
//   TableRowColumn,
//   TableHead,
//   TableCell
// } from 'material-ui/Table'
import * as firebase from 'firebase';
import TrashIcon from 'material-ui/svg-icons/action/delete';
import TabIndicator from '@material-ui/core/Tabs/TabIndicator';
import { throws } from 'assert';
import { Tab } from '@material-ui/core';


class ViewJobs extends Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      JobInfo: [{
        Email: '',
        CompanyId: '', jobtitle: '', description: '', salary: '', key: ''
      }]

    }
  }

  componentDidMount() {
    //var currentUserType=firebase.auth().currentUser.type;
    var currentUserEmail = firebase.auth().currentUser.e_mail
    console.log('currentuser email' + currentUserEmail)
    var uid = firebase.auth().currentUser.uid
    const rootRef = firebase.database().ref();
    var speedRef = rootRef.child('Job/');
    speedRef.on('value', snap => {
      var uid = firebase.auth().currentUser.e_mail;
      var userobj = snap.val();
      var key = Object.keys(userobj);
      this.setState({ key })
      for (var 
        i = 0; i < key.length; i++) {
        var k = key[i];

        // console.log('a',userobj[k]);

        if (userobj[k]) {
          this.state.JobInfo[i] = {};

          this.state.JobInfo[i].CompanyId = userobj[k].CompanyId;
          this.state.JobInfo[i].jobtitle = userobj[k].jobtitle;
          this.state.JobInfo[i].description = userobj[k].description;
          this.state.JobInfo[i].salary = userobj[k].salary;

        }
        else { console.log("anything", userobj[i].JobInfo) }
      }


      // if(userobj[k].type=='Admin') {

      // }


      this.setState({ JobInfo: this.state.JobInfo })
      this.setState({key:this.state.key})
    })
  }

  DeleteRecord(i){
    var indexValue=this.state.key[i] 
    firebase.database().ref('Job/')    
    // var refDB = firebase.database().ref().child('Job');
    //     refDB.once("value")
    //     .then(function(snapshot) {
    //         snapshot.ref.remove();
    //         console.log("Record deleted..!");
    // }).catch(function(error) {console.log("Data could not be deleted." + error);});
} 


  // deleteJob(i) {
  //   console.log('in delete job')
  //   var indexValue = this.state.key[i]
  //   firebase.database().ref('Job/' + indexValue).remove();
  // }
  // DeleteJob() {
  //   console.log('in delete jon')
  // var db = firebase.database();                   
  // var ref = db.ref(); 
  // var survey=db.ref('Job'+'/'+'USER');    //Eg path is company/employee                
  // survey.child('Job').remove();          //Eg key is employee id
  // }

// DeleteJob() {
//   console.log('in delete job')

//   // var adaRef = firebase.database().ref('users/ada');
//   // adaRef.remove()
//   //   .then(function() {
//   //     console.log("Remove succeeded.")
//   //   })
//   //   .catch(function(error) {
//   //     console.log("Remove failed: " + error.message)
//   //   });

// }





  Apply(index){
    console.log('in apply job')
     var currentUser=firebase.auth().currentUser;
      var currentId=firebase.auth().currentUser.uid;
      var rootRef=firebase.database().ref();
      const speedRef=rootRef.child('USER/' +currentId);
      speedRef.on("value",snap => {
        var values=snap.val();

        let obj=(snap.val() || {
          LName: values.lName,
          e_mail: values.e_mail,
          userId: currentId
        })
         rootRef=firebase.database().ref();
         speedRef=rootRef.child("Job/" +this.state.key[index]+"/Apply/" +currentId).set(obj)


      })


  }


  render() {


    // console.log('jobtitle', this.state.JobInfo)

    return (


      <Paper style={{backgroundColor:'#BDBDBD'}}>
        <Table id="JobTable">
          <TableHead>
            <TableRow>
              <TableCell>Company Name</TableCell>
              <TableCell >Job Title</TableCell>
              <TableCell >Description</TableCell>
              <TableCell >Salary</TableCell>
              {/* <TableCell ><RaisedButton></RaisedButton></TableCell> */}
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.JobInfo.map(row => {
              return (
                <TableRow key={row.id}>

               <TableCell >{row.CompanyId}</TableCell>
                  <TableCell >{row.jobtitle}</TableCell>
                  <TableCell >{row.description}</TableCell>





                  {this.state.id == 0 ? <TableCell >{row.salary}</TableCell> : <TableCell >xxxx</TableCell>}
                  {this.state.id == 0 ? <TableCell >


                  </TableCell> : <TableCell ></TableCell>}
                   <TableCell> 
                    <Button onClick={(event) => this.DeleteRecord(event)} >delete job</Button>
                    <Button onClick={(event) => this.Apply(event)} >Apply for job</Button> 






                  </TableCell> 




                </TableRow>

              );

            })}
          </TableBody>
        </Table>
      </Paper>

    )
  }

}


const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});


export default withStyles(styles)(ViewJobs);
