import React, { Component } from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';
import * as firebase from 'firebase';
import ViewJobs from './ViewJobs';

class JobPost extends Component {
    constructor() {
        super();
        this.state = {
            jobtitle: [],
            description: [],
            Salary: []


        }
    }

    PostJob() {
        var uid=firebase.auth().currentUser=uid;

    }

    render() {
        return (
            <div>
                <MuiThemeProvider>
                    <div>
                        <AppBar style={{ backgroundColor: '#212121' }} title="Post New Job" />
                        <TextField
                            hindText='Job Title'
                            floatingLabelText='Job Title'
                            value={this.state.jobtitle}
                            onChange={this.inputChange.bind(this.jobtitle)}
                            floatingLabelText
                        />
                        <br></br>
                        <TextField
                            hindText='Job-Description'
                            floatingLabelText='Desciption'
                            value={this.state.description}
                            onChange={this.inputChange.bind(this, description)}
                            floatingLabelText
                        />
                        <br></br>
                        <TextField
                            hindText='Salary'
                            floatingLabelText='Salary'
                            value={this.state.Salary}
                            onChange={this.inputChange.bind(this.Salary)}

                        />
                        <br></br>
                        <Button variant="contained" onClick={(event) => this.handleClick(event)}><b>Post Job</b></Button>

                    </div>

                </MuiThemeProvider>
            </div>
        )
    }


}
export default JobPost;
