import React, { Component } from 'react';
import PropTypes, { func } from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import *as firebase from 'firebase'
import { create } from 'jss';


class MyPostJobs  extends Component {

    componentDidMount() {
        console.log("in did mount job post")
        var uid = firebase.auth().currentUser.uid;
        const rootRef = firebase.database().ref();
        const speedRef = rootRef.child('Job/');
        speedRef.on('value', snap => {
            var userobj = snap.val();
            var key = Object.keys(userobj);
            this.setState({ key })
            for (var i = 0; i < key.length; i++) {
                var k = key[i];
                if (uid == userobj[k].CompanyId) {
                    this.state.jobtitle[i] = userobj[k].jobtitle;
                    this.state.description[i] = userobj[k].description;
                    this.state.salary[i] = userobj[k].salary;
                }
            }
        }
        )
    }

     SimpleTable(props) {
        const { classes } = props;
        return (
            <Paper>
                <Table  >
                    <TableHead>
                        <TableRow>
                        <TableCell>Company Name</TableCell>
                            <TableCell>Job Title</TableCell>
                            <TableCell>Description</TableCell>
                            <TableCell>Salary</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.map(row => {
                            return (
                                <TableRow key={row.id}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell numeric>{row.name}</TableCell>
                                    <TableCell numeric>{row.JobTitle}</TableCell>
                                    <TableCell numeric>{row.Description}</TableCell>
                                    <TableCell numeric>{row.Salary}</TableCell>
                                </TableRow>
                            );
                        })}
                    </TableBody>
                </Table>
            </Paper>
        );
    }
    render(){
        console.log("IN RENDER")
        return(
                <div>{this.SimpleTable()}</div>    
        )}
}
const styles = theme => ({
    root: {
