import React, { Component } from 'react';
import TextField from 'material-ui/TextField'
import { Link } from 'react-router-dom'
import RaisedButton from 'material-ui/RaisedButton';
import * as firebase from 'firebase';

class Login extends Component {
    constructor() {
        super();
        this.state = {
            myInfo: {
                fName: '',
                lName: '',
                e_mail: '',
                password: '',
                cPassword: '',
                country: '',
                city: '',
                Contact_Number: '',
                Passport_Number: '',
                NIC_Number: ''

            },
            fields: [],
            error: 'this is error'
        }
    }

    handleClick(event) {
        firebase.auth().signInWithEmailAndPassword(this.state.myInfo.fName,this.state.myInfo.password).then((error) => {
            var typeCheck;
            var userId=firebase.auth().currentUser.uid;

        }
        )
    }

    successMessage(event) {

        this.state.fields.push(this.state.myInfo);
        this.setState({
            fields: this.state.fields
        })
        console.log('hi there', this.state.myInfo)
        event.preventDefault();
    }

    inputChange(changeValue, event) {

        this.state.myInfo[changeValue] = event.target.value;
        console.log('event', event.target.value);
        this.setState({
            myInfo: this.state.myInfo
        });

    }

    render() {
        return (


            <div>
                <form>
                    <TextField
                    name="FName"
                    hintText="First Name"
                    floatingLabelText="First Name"
                    value={this.state.myInfo.fName}
                    onChange={this.inputChange.bind(this,"fName")}
                    floatingLabelFixed
                      />
                    <br></br>


                    <TextField
                    name="password"
                    hintText="Password"
                    floatingLabelText="password"
                    value={this.state.myInfo.password}
                    onChange={this.inputChange.bind(this,"password")}
                    type="password" 
                    floatingLabelFixed
                      />
                    <br></br>



                    <RaisedButton bsStyle="primary" onClick={this.successMessage.bind(this)}><b>login</b></RaisedButton>
                    <Link to={{
                        pathname: '/Login',
                        data: this.state.myInfo

                    }}>
                        <RaisedButton>Not have Account?</RaisedButton>
                    </Link>

                </form>
            </div>

        )

    }

}
export default Login
