import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import *as firebase from 'firebase';
import { join } from 'path';
class ViewStudents extends Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      JobInfo: [{ JobTitle: '', Description: '',Salary: '' }]
    }
  }
  componentDidMount() {

    // var uid = firebase.auth().currentUser.uid;
    var currentUserEmail=  firebase.auth().currentUser.email
    const rootRef = firebase.database().ref();
    var speedeRef = rootRef.child('Job');
    speedeRef.on('value', snap => {

      var count = 0;
      var userobj = snap.val();
      var key = Object.keys(userobj);
      for (var i = 0; i < key.length; i++) {
        var k = key[i];

        console.log(userobj[k].type);
        if (userobj[k].type == 'student') {
          if (userobj[k]) {
            console.log("s", userobj[k].e_mail)
            this.state.e_mail[i] = {};

            this.state.JobInfo[i].JobTitle = userobj[k].JobTitle;
            this.state.JobInfo[i].Description = userobj[k].Description;
            this.state.JobInfo[i].Salary=userobj[k].Salary

          } else { console.log("s", userobj[i].e_mail) }

          //this.state.name[i]=userobj[k],name;
          //console.log("s",userobj[k])

        }
      }
      this.setState({ e_mail: this.state.e_mail })


    })
  }





  // const rows = [
  //   createData('e_mail' ),
  // //   createData('Ice cream sandwich'),
  // //   createData('Eclair'),
  // //   createData('Cupcake'),
  // //   createData('Gingerbread'),
  // ];

  render() {
    // const { classes } = props;
    console.log("email", this.state.e_mail)
    console.log("lName",this.state.lName)
    console.log("GPA",this.state.GPA)
    return (

      <Paper >
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>JobTitle</TableCell>
              <TableCell numeric>Description</TableCell>
              <TableCell numeric>Salary</TableCell>

            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.e_mail.map(row => {
              return (
                <TableRow key={row.id}>


                  <TableCell >{row.JobTitle}</TableCell>
                  <TableCell >{row.Description}</TableCell>
                  <TableCell >{row.Salary} </TableCell>

                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Paper>
    )
  }
}

const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});


export default withStyles(styles)(ViewStudents)




































