import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import *as firebase from 'firebase';


class ViewJobs extends Component{
    constructor() {
        super();
        this.state={
            id: 0,   
            JobInfo: [{JobTitle:'', Description:'', Salary:''}]

    }
}

componentDidMount(){
    const rootRef=firebase.database().ref();
    var speedRef= rootRef.child('Job/');
    speedRef.on('value', snap => {
        var count=0;


    })
}

render() {
      console.log('jobtitle',this.state.JobTitle)
      return (
   <Paper>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Company Name</TableCell>
            <TableCell numeric>Job Title</TableCell>
            <TableCell numeric>Description</TableCell>
            <TableCell numeric>Salary</TableCell>
            </TableRow>
        </TableHead>
        <TableBody>
        {this.state.JobInfo.map(row => {
              return (
                <TableRow key={row.id}>

                <TableCell numeric>{row.calories}</TableCell>
                <TableCell numeric>{row.fat}</TableCell>
                <TableCell numeric>{row.carbs}</TableCell>
                <TableCell numeric>{row.protein}</TableCell>
              </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </Paper>
  )
}

}

const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});


export default withStyles(styles)(ViewJobs);










