    }

}


const styles=theme=> ({




     root: {
         width: '100%',
         marginTop: theme.spacing.unit *3,
         overflowX: 'auto',

     },

     table: {
         minWidth: 700, 
     },
});



let id=0;
function createData(User,JobTitle,Description,Salary) {
    id+=1
    return(id,JobTitle,Description,Salary);
}

const rows= [
    createData('JeddahSoft', this.state.JobTitle , this.state.Description, this.state.Salary ),
    createData('Avanza', this.state.JobTitle , this.state.Description, this.state.Salary),
    createData('xyz',this.state.JobTitle , this.state.Description, this.state.Salary)
];
function SimpleTable(props){
    const {classes} =props;

return (

    <Paper className={classes.root}>
    <Table className={classes.table}>
      <TableHead>
        <TableRow>
          <TableCell>Job Title</TableCell>
          <TableCell>Description</TableCell>
          <TableCell>Salary</TableCell>
        </TableRow>
      </TableHead>
      <TableBody>
        {rows.map(row => {
          return (
            <TableRow key={row.id}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell numeric>{row.JobTitle}</TableCell>
              <TableCell numeric>{row.Description}</TableCell>
              <TableCell numeric>{row.Salary}</TableCell>
              </TableRow>
          );
        })}
      </TableBody>
    </Table>
  </Paper>
);
}

SimpleTable.propTypes= {
    classes: PropTypes.object.isRequired
};
export default withStyles(styles)(SimpleTable)(MyPostJobs)



