import React, {Component} from 'react';
import './SignUp_css.css';
import { Button } from 'react-bootstrap';

class SignUp extends Component{
    constructor(){
      super();
      this.state={
        myInfo : {
            fName:'',
            lName:'',
            e_mail:'',
            password:'',
            cPassword:'',
            country:'',
            city:'',
            Contact_Number:'',
            Passport_Number:'',
            NIC_Number:''

        },
        fields:[],
        error:'this is error'
      }
    }

    successMessage(event){

        this.state.fields.push(this.state.myInfo);
        this.setState({
            fields : this.state.fields
        })
        console.log('hi there',this.state.myInfo )
        event.preventDefault();
    }

    inputChange(changeValue,event){

        this.state.myInfo[changeValue] = event.target.value;
        console.log('event', event.target.value);
    this.setState({
            myInfo : this.state.myInfo
        });

    }

    render(){
    return(


<div>
     <form className="signUp_css">
     <br></br>
     <label><b>User Name</b></label>
     <input className="inputField" type="text" value={this.state.myInfo.fName} onChange={this.inputChange.bind(this,"fName")}

     required={true} 
     minLength={4}
     maxLength={20}
        />
        <br></br>
        <br></br>
        <label><b>Password</b></label>
        <input type="password" value={this.state.myInfo.password} onChange={this.inputChange.bind(this,"password"
        )}
     required={true} 
     minLength={8}
     maxLength={20}
     />
     <br></br>
     <br></br>
    <label><b>email</b></label>
     <input type="email" value={this.state.myInfo.e_mail} onChange={this.inputChange.bind(this,"e_mail"
        )}
     required={true} 
     minLength={15}
     maxLength={20}
     />
     <br></br>
     <br></br>


            <Button bsStyle="primary" onClick={this.successMessage.bind(this)}><b>login</b></Button>
   </form>
   </div>
)

}

}

<div>
    <form className='signUp_css'>
    <br></br>
    <label><b></b></label>



    </form>
</div>


export default SignUp
