  //     })


  // }


  render() {


    // console.log('jobtitle', this.state.JobInfo)

    return (

      <Paper style={{backgroundColor:'#BDBDBD'}}>
        <Table id="JobTable">
          <TableHead>
            <TableRow>
              <TableCell>Company Name</TableCell>
              <TableCell >Job Title</TableCell>
              <TableCell >Description</TableCell>
              <TableCell >Salary</TableCell>
              {/* <TableCell ><RaisedButton></RaisedButton></TableCell> */}
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.JobInfo.map(row => {
              return (
                <TableRow key={row.id}>

               <TableCell >{row.CompanyId}</TableCell>
                  <TableCell >{row.jobtitle}</TableCell>
                  <TableCell >{row.description}</TableCell>





                  {this.state.id == 0 ? <TableCell >{row.salary}</TableCell> : <TableCell >xxxx</TableCell>}
                  {this.state.id == 0 ? <TableCell >


                  </TableCell> : <TableCell ></TableCell>}
                  <TableCell> <Button onClick={this.DeleteJob().bind.this} >delete job</Button>




                  </TableCell>

                </TableRow>

              );

            })}
          </TableBody>
        </Table>
      </Paper>
    )
  }

}


const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});


export default withStyles(styles)(ViewJobs);
