import React, {Component} from 'react';
import * as firebase from 'firebase';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';

class StudentDetails extends Component{

    constructor() {
        super();
    this.state={
        user: '',
        Name: '',
        DegreeTitle: '',
        UniversityName:'',
        FromDate:'',
        ToDate:'',
        GPA: '',
        Division:'',
        EnrollmentNumber:''
    }
    }

    componentDidMount(){
        var userId=firebase.auth().currentUser.uid;
       firebase.database().ref('USER'+ '/'+userId).set({
           ...this.state.user,
           name:this.state.name,
           DegreeTitle:this.state.DegreeTitle,
           UniversityName:this.state.UniversityName,
           FromDate:this.state.FromDate,
           ToDate:this.state.ToDate,
           GPA:this.state.GPA,
           Division:this.state.div,
           EnrollmentNumber:this.state.EnrollmentNumber
       })
    }
    render() {
        return(

    <div>
<MuiThemeProvider>
    <div>
        <AppBar style={{BackgroundColor: '#212121'}} />
        <TextField
             hintText="Enter your Full Name"
             floatingLabelText="name"
             onChange = {(event,newValue) => this.setState({name:newValue})}
             />
           <br/>
           <TextField
             hintText="Degree Title"
             floatingLabelText="Degree Title"
             onChange = {(event,newValue) => this.setState({DegreeTitle:newValue})}
             />
           <br/>
           <TextField
             hintText="University Name"
             floatingLabelText="University Name"
             onChange = {(event,newValue) => this.setState({UniversityName:newValue})}
             />
           <br/>
           <TextField
             hintText="From Date"
             floatingLabelText="From Date"
             onChange = {(event,newValue) => this.setState({FromDate:newValue})}
             />
           <br/>
           <TextField
             hintText="GPA"
             floatingLabelText="GPA"
             onChange = {(event,newValue) => this.setState({GPA:newValue})}
             />
           <br/>
           <TextField
             hintText="Division"
             floatingLabelText="Division"
             onChange = {(event,newValue) => this.setState({Division:newValue})}
             />
           <br/>
           <TextField
             hintText="Enrollment Number"
             floatingLabelText="Name"
             onChange = {(event,newValue) => this.setState({fullName:newValue})}
             />
           <br/>
           <TextField
             hintText="Enter your Full Name"
             floatingLabelText="Name"
             onChange = {(event,newValue) => this.setState({fullName:newValue})}
             />
           <br/>
    </div>
</MuiThemeProvider>

    </div>


        )
    }
}
export default StudentDetails;
