import React, {Component} from 'react';
import './SignUp_css.css';
import { Link } from 'react-router-dom'
import { Button } from 'react-bootstrap';

class Login extends Component{
    constructor(){
      super();
      this.state={
        myInfo : {
            fName:'',
            lName:'',
            e_mail:'',
            password:'',
            cPassword:'',
            country:'',
            city:'',
            Contact_Number:'',
            Passport_Number:'',
            NIC_Number:''

        },
        fields:[],
        error:'this is error'
      }
    }

    successMessage(event){

        this.state.fields.push(this.state.myInfo);
        this.setState({
            fields : this.state.fields
        })
        console.log('hi there',this.state.myInfo )
        event.preventDefault();
    }

    inputChange(changeValue,event){

        this.state.myInfo[changeValue] = event.target.value;
        console.log('event', event.target.value);
    this.setState({
            myInfo : this.state.myInfo
        });

    }
    componentDidMount(){
        console.log('componentDidMount')
      }
      componentDidCatch() {
        console.log('componentDidCatch')
      }
       componentDidUpdate () {
         console.log('componentDidUpdate')
      }
      componentWillMount() {
        console.log('componentWillMount')
      }
      componentWillReceiveProps({ myInfo}) {
        console.log('componentWillReceiveProps', myInfo )
      }
      componentWillUnmount() {
        console.log('componentWillUnmount')
      }
       componentWillUpdate(){
         console.log('componentWillUpdate')

       }



render(){
    return(


<div>
    <form className='signUp_css'>
    <br></br>
    <label><b>Name</b></label>
    <input className="inputField" type="text" value={this.state.myInfo.fName} onChange={this.inputChange.bind(this,"fName")}

required={true} 
minLength={10}
maxLength={20}
   />
   <br></br>
   <br></br>


<label><b>Password</b></label>
        <input type="password" value={this.state.myInfo.password} onChange={this.inputChange.bind(this,"password"
        )}
     required={true} 
     minLength={8}
     maxLength={20}
     />
      <br></br>
      <br></br>




<Button bsStyle="primary" onClick={this.successMessage.bind(this)}><b>login</b></Button>
<Link to={{
    pathname:'/Login',
    data:this.state.myInfo

}}>
<Button>Not have Account?</Button>






</Link>



    </form>
</div>

    )

}

}
export default Login
