import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import *as firebase from 'firebase';


class ViewJobs extends Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      JobInfo: [{
        CompanyId: '', jobtitle: '', description: '', salary: ''
      }]

    }
  }

  componentDidMount() {

    var currentUserEmail= firebase.auth().currentUser.e_mail
    console.log( 'email' +currentUserEmail)
    var uid=firebase.auth().currentUser
    const rootRef = firebase.database().ref();
    var speedRef = rootRef.child('Job/');
    speedRef.on('value', snap => {
      var count = 0;
      var userobj = snap.val();
      var key = Object.keys(userobj);
      for (var i = 0; i < key.length; i++) {
        var k = key[i];

        console.log(userobj[k].type);
        if (userobj[k].type == 'student') {
          if (userobj[k]) {
           this.state.JobInfo[i] = {};

            this.state.JobInfo[i].CompanyId = userobj[k].CompanyId;
            console.log("Company ID  at :"+ i +  this.state.companyId[i])
            this.state.JobInfo[i].jobtitle = userobj[k].jobtitle;
            console.log("job title  at :"+ i +  this.state.jobtitle[i])
            this.state.JobInfo[i].description = userobj[k].description;
            console.log("description :"+ i +  this.state.description[i])
            this.state.JobInfo[i].salary = userobj[k].salary;
            console.log("salary :"+ i +  this.state.salary[i])

          }

        }

      }
// if(userobj[k].type=='Admin') {

// }


      this.setState({ JobInfo: this.state.JobInfo })
    })
  }

  render() {
    console.log('jobtitle', this.state.JobInfo.jobtitle)
    console.log('description', this.state.JobInfo.description)
    console.log('salary', this.state.JobInfo.salary)
    return (

      <Paper>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Company Name</TableCell>
              <TableCell >Job Title</TableCell>
              <TableCell >Description</TableCell>
              <TableCell >Salary</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.JobInfo.map(row => {
              return (
                <TableRow key={row.id}>

                  <TableCell >{row.CompanyId}</TableCell>
                  <TableCell >{row.jobtitle}</TableCell>
                  <TableCell >{row.description}</TableCell>
                  <TableCell >{row.salary}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </Paper>
    )
  }

}

const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});


export default withStyles(styles)(ViewJobs);










