import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import *as firebase from 'firebase';
import { join } from 'path';
class ViewStudents extends Component{
    constructor() {
        super();
        this.state={
            id:0,
            e_mail:[{email:'',name:''},{email:'',name:''}]
        }
    }
    componentDidMount() {

   // var uid = firebase.auth().currentUser.uid;
    const rootRef=firebase.database().ref();
    var speedeRef=rootRef.child('USER');
    speedeRef.on('value', snap => {

        var count=0;
    var userobj=snap.val();
    var key=Object.keys(userobj);
    for (var i=0;  i<key.length; i++) {
        var k=key[i];

        console.log(userobj[k].type);
        if(userobj[k].type=='student'){
           if(userobj[k]){
            console.log("s",userobj[k].e_mail)
            this.state.email[i]= {};
            this.state.email.
             this.state.e_mail[i].email=userobj[k].e_mail;
             this.state.e_mail[i].name=userobj[k].fName;
           }else{  console.log("s",userobj[i].e_mail)}

            //this.state.name[i]=userobj[k],name;
            //console.log("s",userobj[k])

        }}
        this.setState({e_mail:this.state.e_mail})


    } )
}





// const rows = [
//   createData('e_mail' ),
// //   createData('Ice cream sandwich'),
// //   createData('Eclair'),
// //   createData('Cupcake'),
// //   createData('Gingerbread'),
// ];

render(){
 // const { classes } = props;
  console.log("email",this.state.e_mail)
  return (

    <Paper >
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Student Name</TableCell>
            <TableCell numeric>Email</TableCell>
            <TableCell numeric>Degree Title</TableCell>
            <TableCell numeric>University Name</TableCell>
            <TableCell numeric>From Date</TableCell>
            <TableCell numeric>To Date</TableCell>
            <TableCell numeric>GPA</TableCell>
            <TableCell numeric>Division</TableCell>
            <TableCell numeric>Enrollment Number</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {this.state.e_mail.map(row => {
            return (
              <TableRow key={row.id}>

                <TableCell >{row.email}</TableCell>
                <TableCell >{row.name}</TableCell>

              </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </Paper>
  )}
}

const styles = theme => ({
    root: {
      width: '100%',
      marginTop: theme.spacing.unit * 3,
      overflowX: 'auto',
    },
    table: {
      minWidth: 700,
    },
  });


export default withStyles(styles)(ViewStudents)
