import React, { Component } from 'react';
import * as firebase from 'firebase';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';

class StudentDetails extends Component {

    constructor() {
        super();
        this.state = {
            studentdetails: {
            user: '',
            Name: '',
            DegreeTitle: '',
            UniversityName: '',
            FromDate: '',
            ToDate: '',
            GPA: '',
            Division: '',
            EnrollmentNumber: ''
        }
    }

}
    componentDidMount() {
        console.log('student detail')



        var userId = firebase.auth().currentUser.uid;
        const rootRef = firebase.database().ref();
        const speedRef = rootRef.child('USER/' + userId);
        var userobj;
        speedRef.on('value', snap => {
            userobj = snap.val();

            this.setState({
                user: userobj

            })

        })
    }

    UpdateStudentDetails(event) {
        var uid = firebase.auth().currentUser.uid;
        firebase.database().ref('StudentDetails/').push({
            ...this.state.user,
            name: this.state.name,
            DegreeTitle: this.state.DegreeTitle,
            UniversityName: this.state.UniversityName,
            FromDate: this.state.FromDate,
            ToDate: this.state.ToDate,
            GPA: this.state.GPA,
            Division: this.state.Division,
            EnrollmentNumber: this.state.EnrollmentNumber,
            StudentId:uid
        })
    }
    nputChange(changeValue, event) {
        this.state[changeValue] = event.target.value;
        console.log('event', event.target.value);
        this.setState({
           studentdetails: this.state.studentdetails
       });

    }

    render() {
        return (

            <div>
                <MuiThemeProvider style={{ backgroundColor: '#212121' }}>
                    <div  style={{ backgroundColor: '#BDBDBD' }}>>
                        <AppBar />
                        <TextField
                            hintText="Enter your Full Name"
                            floatingLabelText="name"
                            onChange={this.inputChange.bind(this, "name")}
                        />
                        <br />
                        <TextField
                            hintText="Degree Title"
                            floatingLabelText="Degree Title"
                            onChange={this.inputChange.bind(this, "DegreeTitle")}
                        />
                        <br />
                        <TextField
                            hintText="University Name"
                            floatingLabelText="University Name"
                            onChange={this.inputChange.bind(this, "UniversityName")}
                        />
                        <br />
                        <TextField
                            hintText="From Date"
                            floatingLabelText="From Date"
                            onChange={this.inputChange.bind(this, "FromDate")}
                        />
                        <TextField
                            hintText="To Date"
                            floatingLabelText="To Date"
                            onChange={this.inputChange.bind(this, "ToDate")}
                        />
                        <br />
                        <br />
                        <TextField
                            hintText="GPA"
                            floatingLabelText="GPA"
                            onChange={this.inputChange.bind(this, "GPA")}
                        />
                        <br />
                        <TextField
                            hintText="Division"
                            floatingLabelText="Division"
                            onChange={this.inputChange.bind(this, "Division")}
                        />
                        <br />
                        <TextField
                            hintText="Enrollment Number"
                            floatingLabelText="Enrollment Number"
                            onChange={this.inputChange.bind(this, "EnrollmentNumber")}
                        />
                        <br />

                        <br />
                        <RaisedButton label='Update' primary={true} onClick={(event) => this.UpdateStudentDetails(event)} style />
                    </div>
                </MuiThemeProvider>

            </div>


        )
    }
}
export default StudentDetails;
