import React, { Component } from 'react';
import TextField from 'material-ui/TextField'
import { Link } from 'react-router-dom'
import { Button } from 'react-bootstrap';
import * as firebase from 'firebase';

class Login extends Component {
    constructor() {
        super();
        this.state = {
            myInfo: {
                fName: '',
                lName: '',
                e_mail: '',
                password: '',
                cPassword: '',
                country: '',
                city: '',
                Contact_Number: '',
                Passport_Number: '',
                NIC_Number: ''

            },
            fields: [],
            error: 'this is error'
        }
    }

    handleClick(event) {
        firebase.auth().signInWithEmailAndPassword(this.state.myInfo.e_mail,this.state.myInfo.password).then((error) => {
            var errorCode = error.code;
