import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import *as firebase from 'firebase';
class ViewStudents extends Component{
    constructor() {
        super();
        this.state={
            id:0
        }
    }
    componentDidMount() {
//    var uid = firebase.auth().currentUser.uid;
    const rootRef=firebase.database().ref();
    var speedeRef=rootRef.child('USER');
    speedeRef.on('value', snap => {

        var count=0;
    var userobj=snap.val();
    var key=Object.keys(userobj);
    for (var i=0;  i<key.length; i++) {
        var k=key[i];
        console.log(userobj[k].type);
        if(userobj[k].type=='student') {
            this.state.e_mail[i]=userobj[k].Email;
            this.state.Name[i]=userobj[k],Name;

        }
    }
    } )

}

}

const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});

let id = 0;
function createData(fName, e_mail, DegreeTitle, UniversityName, FromDate, ToDate, GPA, Division,EnrollmentNumber) {
  id += 1;
  return { id, Name, e_mail, DegreeTitle, UniversityName, FromDate, ToDate, GPA, Division,EnrollmentNumber };
}

const rows = [
  createData('Frozen yoghurt' ),
  createData('Ice cream sandwich'),
  createData('Eclair'),
  createData('Cupcake'),
  createData('Gingerbread'),
];

function SimpleTable(props) {
  const { classes } = props;

  return (
    <Paper className={classes.root}>
      <Table className={classes.table}>
        <TableHead>
          <TableRow>
            <TableCell>Student Name</TableCell>
            <TableCell numeric>Email</TableCell>
            <TableCell numeric>Degree Title</TableCell>
            <TableCell numeric>University Name</TableCell>
            <TableCell numeric>From Date</TableCell>
            <TableCell numeric>To Date</TableCell>
            <TableCell numeric>GPA</TableCell>
            <TableCell numeric>Division</TableCell>
            <TableCell numeric>Enrollment Number</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => {
            return (
              <TableRow key={row.id}>
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell numeric>{row.name}</TableCell>
                <TableCell numeric>{row.e_mail}</TableCell>
                <TableCell numeric>{row.DegreeTitle}</TableCell>
                <TableCell numeric>{row.UniversityName}</TableCell>
                <TableCell numeric>{row.FromDate}</TableCell>
                <TableCell numeric>{row.ToDate}</TableCell>
                <TableCell numeric>{row.GPA}</TableCell>
                <TableCell numeric>{row.Division}</TableCell>
                <TableCell numeric>{row.EnrollmentNumber}</TableCell>
              </TableRow>
            );
          })}
        </TableBody>
      </Table>
    </Paper>
  );
}

SimpleTable.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SimpleTable);
