import React, {Component} from 'react';
import './signup.css';
import { Button } from 'react-bootstrap';

class SignUp extends Component{
    constructor(){
      super();
      this.state={
        myInfo : {
            fName:'',
            lName:'',
            e_mail:'',
            password:'',
            cPassword:''
        },
        fields:[],
        error:'this is error'
      }
    }

    successMessage(event){

        this.state.fields.push(this.state.myInfo);
        this.setState({
            fields : this.state.fields
        })
        console.log('hi there',this.state.myInfo )
        event.preventDefault();
    }

    inputChange(changeValue,event){

        this.state.myInfo[changeValue] = event.target.value;
        console.log('event', event.target.value);
    this.setState({
            myInfo : this.state.myInfo
        });

    }

    render(){
    return(


<div>
    <form className="signup.css">
    <br></br>
    <label><b>User Name</b></label>
    <input className="input field" type="text" value={this.state.myInfo.fName} onChange={this.inputChange.bind(this,"fName")}
    required={true} ></input>




    </form>



</div>
)

}

}
