render(){
    return(
        <Router>
                  <div>
          {this.state.user}
           <div>
           <h1>Company</h1>
           <h2>{this.state.user}</h2>
            <style backgroundColor="">

            </style>
             <p><Link  style={{backgroundColor:'gray', border:'10px'}} className="link" to="/company/JobPost">Post Job</Link></p>
              <p><Link className="link" to="/company/MyPostJobs">My Post Job</Link></p>
             <p><Link className="link" to ="/company/ViewAllStudents"> View Students</Link></p>

           </div>
            {/* // :
            //    <h4>please wait. . </h4>
            } */}
        <Route  style={{backgroundColor:'#212121'}} path="/company/JobPost" component={JobPost}/>
         <Route path="/company/MyPostJobs" component={MyPostJobs}/>
        <Route path="/company/ViewStudents" component={ViewStudents}/> 
        </div>
        </Router>

    );
 }
}
